event.script:
/* Swap the two. */
if(!SwapLookups(crmForm.all.from, crmForm.all.to))
{
	/* Unsuccessful swap-- restore the original incoming/outgoing direction. */
	crmForm.all.directioncode.DataValue = !crmForm.all.directioncode.DataValue;
}

if (crmForm.FormType == 1) {
    crmForm.all.scheduledend.DataValue = new Date();

    if (window.opener &#38;&#38; window.opener.document.getElementById("ViewConfig_EntityName")) {
        var ermForm = window.opener.EntityForm;
        switch (window.opener.document.getElementById("ViewConfig_EntityName").value) {
        case "Firm":
            if (window.opener.Ext.getCmp("Client").getValue())
                crmForm.all.to.DataValue = [{ id: ermForm.ClientReplicationCode.value, name: window.opener.Ext.getCmp("Client").getValue().name, typename: "account" }];
            break;
        case "Contact":
            if (window.opener.Ext.getCmp("Client").getValue())
                crmForm.all.to.DataValue = [{ id: ermForm.ClientReplicationCode.value, name: window.opener.Ext.getCmp("Client").getValue().name, typename: "account" }, { id: ermForm.ReplicationCode.value, name: ermForm.FullName.value, typename: "contact" }];
            break;
        case "Deal":
            if (window.opener.Ext.getCmp("Client").getValue())
                crmForm.all.to.DataValue = [{ id: ermForm.ClientReplicationCode.value, name: window.opener.Ext.getCmp("Client").getValue().name, typename: "account" }];
            break;
        case "Client":
            crmForm.all.to.DataValue = [{ id: ermForm.ReplicationCode.value, name: ermForm.Name.value, typename: "account" }];
            break;
        }
    }
}

crmForm.AutoCompleteSubject = function() 
{
	if(crmForm.all.subject.value.length == 0 &#38;&#38; crmForm.all.regardingobjectid.DataValue &#38;&#38; crmForm.all.regardingobjectid.DataValue.length &#62; 0 &#38;&#38; crmForm.all.dg_purpose.value)
	{
		var selectedPurpose;
		var purposeOptions = crmForm.all.dg_purpose.options;
		for(var i = 0; i &#60; purposeOptions.length; i++)
		{
			if(crmForm.all.dg_purpose.options[i].selected)
			{
				selectedPurpose = crmForm.all.dg_purpose.options[i].text;
				break;
			}
		}
	crmForm.all.subject.value = crmForm.all.regardingobjectid.DataValue[0].name + ' - ' + selectedPurpose;
	}
}

crmForm.dg_purpose.onchange = function() { crmForm.AutoCompleteSubject(); }
crmForm.regardingobjectid.onchange = function() {crmForm.AutoCompleteSubject();}
