crmForm.AutoCompleteSubject = function() 
{
	if(crmForm.all.subject.value.length == 0 &#38;&#38; crmForm.all.regardingobjectid.DataValue &#38;&#38; crmForm.all.regardingobjectid.DataValue.length &#62; 0 &#38;&#38; crmForm.all.dg_purpose.value)
	{
		var selectedPurpose;
		var purposeOptions = crmForm.all.dg_purpose.options;
		for(var i = 0; i &#60; purposeOptions.length; i++)
		{
			if(crmForm.all.dg_purpose.options[i].selected)
			{
				selectedPurpose = crmForm.all.dg_purpose.options[i].text;
				break;
			}
		}
	crmForm.all.subject.value = crmForm.all.regardingobjectid.DataValue[0].name + ' - ' + selectedPurpose;
	}
}

crmForm.UpdateAfterSaleTypeFieldVisibility = function()
{
    var purpose = crmForm.all.dg_purpose.value;
    var ppsComboBox = crmForm.all.dg_aftersaletype;

    if(purpose == 7 || purpose == 8) {
        crmForm.all.dg_aftersaletype_c.style.visibility = 'visible';
        crmForm.all.dg_aftersaletype.style.visibility = 'visible';

        var needToChangeSelected = false;
        for(var i = 1; i &#60; ppsComboBox.length; i++) {

          // Для "Допродажа" должно отображаться ППС4, для "Сервис" -- ППС1-3.
            ppsComboBox[i].disabled = (purpose == 7) ? i &#60; 4 : i &#62; 3;
            if(ppsComboBox[i].selected == true &#38;&#38; ppsComboBox[i].disabled) {
                 needToChangeSelected = true;}
        }
        if(needToChangeSelected) {
             ppsComboBox[(purpose == 7) ? 4 : 1].selected = true;
        }
    }
    else {
        crmForm.all.dg_aftersaletype_c.style.visibility = 'hidden';
        crmForm.all.dg_aftersaletype.style.visibility = 'hidden';
    }
}

crmForm.UpdateAfterSaleTypeFieldVisibility();

crmForm.dg_purpose.onchange = function() { 
    crmForm.AutoCompleteSubject(); 
    crmForm.UpdateAfterSaleTypeFieldVisibility();
}
crmForm.regardingobjectid.onchange = function() {crmForm.AutoCompleteSubject();}

