event.script:
switch(crmForm.FormType) 
{
    case 1: // create form
        {
            crmForm.all.scheduledend.DataValue = new Date();
        }
        break;
        
    case 4: // disabled form; activity is closed
        {
            var menuElements = crmForm.document.getElementById("crmMenuBar").all;
            for(var i = 0; i &#60; menuElements.length; i++)
            {
                 if(menuElements[i].title == "Отменить задачу")
                 {
                    menuElements[i].style.display = "none";
                    break;
                 }
            }
        }
        break;

    case 0: // undefined form type, barf
    case 2: // update form; activity is open
    case 3: // read only form
    case 5: // quick create form
    case 6: // bulk edit form 
        break;

    default:
        break;
}
