Button.JavaScript:
SetStateCancelled = function (entityId) {     
    var status = 6;
    var state = "Canceled";
    var stateReq = "SetStateTaskRequest";
    var stateType = "Task";
    var xml = "" +
    "<?xml version=\"1.0\" encoding=\"utf-8\"?>" +
    "<soap:Envelope xmlns:soap=\"http://schemas.xmlsoap.org/soap/envelope/\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\">" +
    GenerateAuthenticationHeader() +
    "  <soap:Body>" +
    "    <Execute xmlns=\"http://schemas.microsoft.com/crm/2007/WebServices\">" +
    "      <Request xsi:type=\"" + stateReq + "\">" +
    "        <EntityId>" + entityId + "</EntityId>" +
    "        <" + stateType + "State>" + state + "</" + stateType + "State>" +
    "        <" + stateType + "Status>" + status + "</" + stateType + "Status>" +
    "      </Request>" +
    "    </Execute>" +
    "  </soap:Body>" +
    "</soap:Envelope>" +
    "";

    var xmlHttpRequest = new ActiveXObject("Msxml2.XMLHTTP");
    xmlHttpRequest.Open("POST", "/mscrmservices/2007/CrmService.asmx", false);
    xmlHttpRequest.setRequestHeader("SOAPAction", "http://schemas.microsoft.com/crm/2007/WebServices/Execute");
    xmlHttpRequest.setRequestHeader("Content-Type", "text/xml; charset=utf-8");
    xmlHttpRequest.setRequestHeader("Content-Length", xml.length);
    xmlHttpRequest.send(xml);
    var resultXml = xmlHttpRequest.responseXML;

    var errorNodes = resultXml.selectNodes('//error/description');
    if (errorNodes.length > 0) {         
        return errorNodes[0].text;     
    }
    return null; 
}

mnuBar1.style.display = 'none';
var error = SetStateCancelled(crmForm.ObjectId);

mnuBar1.style.display = 'inline';

if (error) {     
    alert("Ошибка при отмене задачи: " + error);     
}
else { 
    alert("Задача отменена"); history.go(0); 
}

